ruleBookInfo.tocUrl:
text.查看完整目录@href
@js:
index = java.getElements("@@class.directoryArea@p!0").length>6

if(index){
id=result.match(/javascript:.*?\((\d+),(\d+)\)/);
url=book.origin+"/"+id[1]+'_'+id[2]+'/all.html,';
verify=java.getVerificationCode(book.origin+"/mytool/getVerify/?d="+Math.random());
option={
"method":"POST",
"body":"verify="+verify+"&aid="+id[2]
}
result=url+JSON.stringify(option)
}else{result = baseUrl}

ruleToc.chapterList:
id.chapterlist@tag.p!0||class.directoryArea@p!0@a
<js>
if(String(src).match(/验证码不对/)){
result=[{"text":"右上角三点刷新重新获取验证码"}]
}else if(src.match(/请输入验证码/)){	java.setContent(result.toArray().reverse().join(""))
	result = java.getElement("a")
	}
else{result=result}
</js>

